        margin-left: 2rem;
        font-size:x-large;
        font-weight: 600 ;
    }
    .link-coupon{
        margin-left: 2rem;
        color: gray;
    }
    .main-title{
        margin-left: 3rem;
        display: flex;
        align-items: flex-end;
    }
    .title{
        font-size: xx-large;
        font-weight: 600 ;
    }
