.categories {
    display: inline-block;
    padding: 10px 20px; /* Ajusta el padding según tus preferencias */
    background-color: #007bff; /* Color de fondo del botón */
    color: #fff; /* Color del texto del botón */
    border: none; /* Elimina el borde del botón */
    border-radius: 4px; /* Borde redondeado */
    cursor: pointer; /* Cambia el cursor al pasar sobre el botón */
    text-align: center;
    text-decoration: none; /* Elimina la decoración de texto (subrayado) */
}

/* Estilos para el botón al pasar el cursor */
.categories:hover {
    background-color: #0056b3; /* Cambia el color de fondo al pasar el cursor */
}
